CREATE TABLE mytable(
   "Event title"  VARCHAR(44) NOT NULL PRIMARY KEY
  ,"Photo link"   VARCHAR(82) NOT NULL
  ,Day            VARCHAR(9)
  ,Date           DATE  NOT NULL
  ,Time           VARCHAR(8)
  ,Recurring      VARCHAR(5) NOT NULL
  ,"Active event" VARCHAR(5) NOT NULL
  ,Place          VARCHAR(25)
  ,Building       VARCHAR(15)
  ,Organizer      VARCHAR(45) NOT NULL
  ,"Misc tags"    VARCHAR(27)
);
